var prayers  = new function(){console.log('Dynamic prayers forms js loaded');document.addEventListener('DOMContentLoaded',function(){FormFunctions.tidyMultiInputs();});var prevEl='';var q=function(event){var el=event.target;form=el.closest('form');var n=el.getAttribute('name');if(n==''||n==undefined){if(el.closest('.nice-select-dropdown')!=null&&el.closest('.inputwrapper')!=null){el.closest('.inputwrapper').querySelectorAll('select').forEach(select=>{if(el.dataset.value==select.value){el=select;n=select.name;}});}else{return;}}if(el==prevEl){return;}prevEl=el;setTimeout(function(){prevEl='';},50);if(n=='nextBtn'){FormFunctions.nextPrev(1);}else if(n=='prevBtn'){FormFunctions.nextPrev(-1);}prayers.p(el);};window.addEventListener('click',q);window.addEventListener('input',q);this.p=function(el){var n=el.getAttribute('name');var form=el.closest('form');if(n=='name'){let name=FormFunctions.getFieldValue('name',form);if(n=='name'){FormFunctions.changeFieldValue('[name="user_id"]',name,prayers.p,form,);}}};};if(typeof(urlSearchParams)=='undefined'){window.urlSearchParams=new URLSearchParams(window.location.search.replaceAll('&amp;','&'));}Array.from(urlSearchParams).forEach(array=>document.querySelectorAll(`[name^='${array[0]}']`).forEach(el=>FormFunctions.changeFieldValue(el,array[1],prayers.p,el.closest('form'),)));

